class Select{constructor(e){if("string"==typeof e&&(e=document.querySelectorAll(e)),"function"==typeof e[Symbol.iterator])return[...e].map(e=>new Select(e));this.$select=e,this.$visibleLabelText=this.$select.querySelector(".select__visibleText"),this.$hiddenInput=this.$select.querySelector(".select__input"),this.$label=this.$select.querySelector(".select__label"),this.$body=document.querySelector("body"),this.handleClick(),this.hanldeClickWindow()}openState(){console.log("open"),this.$select.classList.add("open")}closeState(){console.log("close"),this.$select.classList.remove("open")}toggleState(){this.$select.classList.contains("open")?(this.closeState(),console.log("click on object")):this.openState()}handleClick(){this.$select.addEventListener("mouseup",()=>{console.log(event.target),this.toggleState(),this.handleValue()})}handleValue(){event.target!=this.$label&&(this.$visibleLabelText.innerText=event.target.innerText,this.$hiddenInput.value=this.$visibleLabelText.innerText,this.$hiddenInput.setAttribute("name",this.$visibleLabelText.innerText))}hanldeClickWindow(){window.addEventListener("click",()=>{event.target!=this.$label&&event.target!=this.$hiddenInput&&(this.closeState(),console.log(event.target))})}}const select=new Select(".select");